import React, { useState } from 'react';
import { useAppData } from './shared';

export function ProblemModal({ problem, onClose }) {
  const { addProblem, updateProblem } = useAppData();
  const [title, setTitle] = useState(problem?.title || '');
  const [description, setDescription] = useState(problem?.description || '');
  const [difficulty, setDifficulty] = useState(problem?.difficulty || 'Easy');
  const [testCases, setTestCases] = useState(problem?.testCases || '');

  const handleSubmit = (e) => {
    e.preventDefault();
    const problemData = { title, description, difficulty, testCases };
    if (problem) {
      updateProblem({ ...problem, ...problemData });
    } else {
      addProblem(problemData);
    }
    onClose();
  };

  return (
    <div className="faculty-modal-backdrop" onClick={onClose}>
      <div className="problem-modal-content" onClick={e => e.stopPropagation()}>
        <h3>{problem ? "Edit Problem" : "Add Problem"}</h3>
        <form onSubmit={handleSubmit}>
          <label>Title</label>
          <input value={title} onChange={e => setTitle(e.target.value)} required />
          <label>Description</label>
          <textarea value={description} onChange={e => setDescription(e.target.value)} required rows={3} />
          <label>Difficulty</label>
          <select value={difficulty} onChange={e => setDifficulty(e.target.value)}>
            <option>Easy</option>
            <option>Medium</option>
            <option>Hard</option>
          </select>
          <label>Test Cases</label>
          <textarea value={testCases} onChange={e => setTestCases(e.target.value)} placeholder="Input: ...\nOutput: ..." rows={5} />
          <div style={{ marginTop: 15 }}>
            <button type="submit" className="modal-primary-btn">Save</button>
            <button onClick={onClose} type="button" className="modal-secondary-btn" style={{ marginLeft: 10 }}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
}
