import React, { useState } from 'react';
import { useAppData } from './shared';

export function ProblemModal({ problem, onClose }) {
  const { addProblem, updateProblem } = useAppData();
  const [title, setTitle] = useState(problem?.title || '');
  const [description, setDescription] = useState(problem?.description || '');
  const [difficulty, setDifficulty] = useState(problem?.difficulty || 'Easy');
  const [testCasesString, setTestCasesString] = useState(problem?.testCasesString || 'Input: \nOutput: ');

  const handleSubmit = (e) => {
    e.preventDefault();
    const problemData = { title, description, difficulty, testCasesString };
    if (problem) {
      updateProblem({ ...problem, ...problemData });
    } else {
      addProblem(problemData);
    }
    onClose();
  };

  // Stop click from closing the modal when clicking inside the content
  const handleContentClick = (e) => e.stopPropagation();

  return (
    <div className="faculty-modal-backdrop" onClick={onClose}>
      <div className="problem-modal-content" onClick={handleContentClick}>
        <h3 className="modal-header-title">{problem ? "Edit Problem" : "Add New Problem"}</h3>
        <form onSubmit={handleSubmit} className="modal-form">
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input id="title" value={title} onChange={e => setTitle(e.target.value)} required />
          </div>
          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea id="description" value={description} onChange={e => setDescription(e.target.value)} required rows={4} />
          </div>
          <div className="form-group">
            <label htmlFor="difficulty">Difficulty</label>
            <select id="difficulty" value={difficulty} onChange={e => setDifficulty(e.target.value)}>
              <option>Easy</option>
              <option>Medium</option>
              <option>Hard</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="testCases">Test Cases</label>
            <textarea id="testCases" value={testCasesString} onChange={e => setTestCasesString(e.target.value)} placeholder="Input: ...&#10;Output: ..." rows={6} />
          </div>
          <div className="modal-actions">
            <button type="button" onClick={onClose} className="modal-secondary-btn">Cancel</button>
            <button type="submit" className="modal-primary-btn">Save Problem</button>
          </div>
        </form>
      </div>
    </div>
  );
}
