import React, { useState } from 'react';
import { Plus, Edit, FileDown } from 'lucide-react';
import { useAppData } from './shared';
import { ContestModal } from './ContestModal';

export function ManageContests() {
  const { contests, submissions, users, malpracticeEvents } = useAppData();
  const [showModal, setShowModal] = useState(false);
  const [editingContest, setEditingContest] = useState(null);

  const openModal = (contest) => {
    setEditingContest(contest || null);
    setShowModal(true);
  };

  return (
    <div className="faculty-card">
      <div className="faculty-card-header">
        <span style={{ fontWeight: 700, fontSize: '1.14em' }}>Manage Contests</span>
        <button className="faculty-action-btn" onClick={() => openModal(null)}>
          <Plus size={20} /> Create Contest
        </button>
      </div>
      <div className="contests-table-head">
        <span>Name</span>
        <span>Actions</span>
      </div>
      <div className="contests-table-list">
        {contests.map((contest) => (
          <div className="contests-table-row" key={contest.id}>
            <span>{contest.name}</span>
            <span>
              <button className="faculty-row-btn" onClick={() => openModal(contest)}><Edit size={16} /> Edit</button>
              {/* Download as CSV Demo Button */}
              <button className="faculty-row-btn" style={{ marginLeft: 8 }}>
                <FileDown size={16} /> Export Marks
              </button>
            </span>
          </div>
        ))}
      </div>
      {showModal &&
        <ContestModal
          contest={editingContest}
          onClose={() => setShowModal(false)}
        />
      }
    </div>
  );
}
