import React, { useState } from 'react';
import { useAppData } from './shared';
import { Plus, X } from 'lucide-react'; // Import icons

export function ProblemModal({ problem, onClose }) {
  const { addProblem, updateProblem } = useAppData();

  // State for the main problem details
  const [title, setTitle] = useState(problem?.title || '');
  const [description, setDescription] = useState(problem?.description || '');
  const [difficulty, setDifficulty] = useState(problem?.difficulty || 'Easy');

  // State for the list of test cases
  const [testCases, setTestCases] = useState(
    problem?.testCases && problem.testCases.length > 0
      ? problem.testCases
      : [{ input: '', output: '' }] // Start with one empty test case
  );

  // --- Test Case Handlers ---

  // Handle changes in any test case input/output field
  const handleTestCaseChange = (index, field, value) => {
    const newTestCases = [...testCases];
    newTestCases[index][field] = value;
    setTestCases(newTestCases);
  };

  // Add a new empty test case (up to a max of 5)
  const handleAddTestCase = () => {
    if (testCases.length < 5) {
      setTestCases([...testCases, { input: '', output: '' }]);
    }
  };

  // Remove a test case by its index
  const handleRemoveTestCase = (index) => {
    if (testCases.length > 1) { // Prevent removing the last one
      const newTestCases = testCases.filter((_, i) => i !== index);
      setTestCases(newTestCases);
    }
  };

  // --- Form Submission ---
  const handleSubmit = (e) => {
    e.preventDefault();
    const problemData = { title, description, difficulty, testCases }; // Now includes the array of test cases
    if (problem) {
      updateProblem({ ...problem, ...problemData });
    } else {
      addProblem(problemData);
    }
    onClose();
  };

  // Stop click from closing the modal
  const handleContentClick = (e) => e.stopPropagation();

  return (
    <div className="faculty-modal-backdrop" onClick={onClose}>
      <div className="problem-modal-content" onClick={handleContentClick}>
        <h3 className="modal-header-title">{problem ? "Edit Problem" : "Add New Problem"}</h3>
        <form onSubmit={handleSubmit} className="modal-form">
          {/* ... Title, Description, Difficulty fields remain the same ... */}
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input id="title" value={title} onChange={e => setTitle(e.target.value)} required />
          </div>
          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea id="description" value={description} onChange={e => setDescription(e.target.value)} required rows={4} />
          </div>
          <div className="form-group">
            <label htmlFor="difficulty">Difficulty</label>
            <select id="difficulty" value={difficulty} onChange={e => setDifficulty(e.target.value)}>
              <option>Easy</option>
              <option>Medium</option>
              <option>Hard</option>
            </select>
          </div>

          {/* --- Dynamic Test Cases Section --- */}
          <div className="form-group">
            <label>Test Cases</label>
            <div className="test-case-list">
              {testCases.map((tc, index) => (
                <div key={index} className="test-case-item">
                  <div className="test-case-header">
                    <span>Test Case {index + 1}</span>
                    {testCases.length > 1 && (
                      <button type="button" className="remove-testcase-btn" onClick={() => handleRemoveTestCase(index)}>
                        <X size={16} />
                      </button>
                    )}
                  </div>
                  <div className="test-case-fields">
                    <textarea
                      placeholder="Input"
                      value={tc.input}
                      onChange={(e) => handleTestCaseChange(index, 'input', e.target.value)}
                      rows={3}
                      required
                    />
                    <textarea
                      placeholder="Expected Output"
                      value={tc.output}
                      onChange={(e) => handleTestCaseChange(index, 'output', e.target.value)}
                      rows={3}
                      required
                    />
                  </div>
                </div>
              ))}
            </div>
            {testCases.length < 5 && (
              <button type="button" className="add-testcase-btn" onClick={handleAddTestCase}>
                <Plus size={16} /> Add Another Test Case
              </button>
            )}
          </div>

          <div className="modal-actions">
            <button type="button" onClick={onClose} className="modal-secondary-btn">Cancel</button>
            <button type="submit" className="modal-primary-btn">Save Problem</button>
          </div>
        </form>
      </div>
    </div>
  );
}
