import React, { useState } from 'react';
import { useAppData } from './shared';
export function ContestModal({ contest, onClose }) {
  const { problems, createContest, updateContest } = useAppData();
  const [name, setName] = useState(contest?.name || '');
  const [startTime, setStartTime] = useState(contest?.startTime || '');
  const [endTime, setEndTime] = useState(contest?.endTime || '');
  const [selectedProblems, setSelectedProblems] = useState(contest?.problemIds || []);

  const handleProblemToggle = (id) => {
    setSelectedProblems(prev =>
      prev.includes(id) ? prev.filter(pid => pid !== id) : [...prev, id]
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = { name, startTime, endTime, problemIds: selectedProblems };
    if (contest) updateContest({ ...contest, ...data });
    else createContest(data);
    onClose();
  };

  return (
    <div className="faculty-modal-backdrop" onClick={onClose}>
      <div className="problem-modal-content" onClick={e => e.stopPropagation()}>
        <h3>{contest ? "Edit Contest" : "Create Contest"}</h3>
        <form onSubmit={handleSubmit}>
          <label>Contest Name</label>
          <input value={name} onChange={e => setName(e.target.value)} required />
          <label>Start Time</label>
          <input type="datetime-local" value={startTime} onChange={e => setStartTime(e.target.value)} required />
          <label>End Time</label>
          <input type="datetime-local" value={endTime} onChange={e => setEndTime(e.target.value)} required />
          <label>Problems</label>
          <div className="contest-problem-box">
            {problems.map(p => (
              <span
                key={p.id}
                className={`contest-problem-label${selectedProblems.includes(p.id) ? " selected" : ""}`}
                onClick={() => handleProblemToggle(p.id)}
              >
                {p.title}
              </span>
            ))}
          </div>
          <div style={{ marginTop: 15 }}>
            <button type="submit" className="modal-primary-btn">Save</button>
            <button onClick={onClose} type="button" className="modal-secondary-btn" style={{ marginLeft: 10 }}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
}
